const current = $json["current_weather"];
const daily = $json["daily"];

// Helper for emoji & color mapping
function getWeatherEmoji(code) {
  if (code === 0) return "☀️ Clear";
  if ([1, 2, 3].includes(code)) return "🌤️ Partly cloudy";
  if ([45, 48].includes(code)) return "🌫️ Foggy";
  if ([51, 53, 55].includes(code)) return "🌦️ Drizzle";
  if ([61, 63, 65].includes(code)) return "🌧️ Rain";
  if ([71, 73, 75].includes(code)) return "❄️ Snow";
  if ([95, 96, 99].includes(code)) return "⛈️ Thunderstorm";
  return "🌈 N/A";
}

function getBackgroundColor(code) {
  if (code === 0) return "#fff7b2";
  if ([1, 2, 3].includes(code)) return "#d9e2ec";
  if ([45, 48].includes(code)) return "#cfd8dc";
  if ([51, 53, 55].includes(code)) return "#b3e5fc";
  if ([61, 63, 65].includes(code)) return "#90caf9";
  if ([71, 73, 75].includes(code)) return "#e1f5fe";
  if ([95, 96, 99].includes(code)) return "#b39ddb";
  return "#e0f7fa";
}

// Today's info
const today = {
  date: daily.time[0],
  max: daily.temperature_2m_max[0],
  min: daily.temperature_2m_min[0],
  wind: daily.windspeed_10m_max[0],
  code: daily.weathercode[0]
};

// Tomorrow's info
const tomorrow = {
  date: daily.time[1],
  max: daily.temperature_2m_max[1],
  min: daily.temperature_2m_min[1],
  wind: daily.windspeed_10m_max[1],
  code: daily.weathercode[1]
};

// Pick background from today's weather
const bgColor = getBackgroundColor(today.code);

return [{
  html: `
  <div style="width:100%; display:flex; justify-content:center; padding:20px;">
    <div style="font-family: Arial, sans-serif; background: ${bgColor}; border-radius: 12px; padding: 16px; max-width: 420px; width: 100%; box-sizing: border-box;">
      <h2 style="text-align:center;">${getWeatherEmoji(today.code)} Karachi Weather</h2>

      <div style="display:flex; justify-content:center; gap:12px; text-align:center;">
        <div style="flex:1; background:white; border-radius:8px; padding:8px;">
          <h3>Today</h3>
          <p><b>📅 Date:</b> ${today.date}</p>
          <p><b>🌡️ Max:</b> ${today.max}°C</p>
          <p><b>🌡️ Min:</b> ${today.min}°C</p>
          <p><b>💨 Wind:</b> ${today.wind} km/h</p>
          <p><b>Condition:</b> ${getWeatherEmoji(today.code)}</p>
        </div>

        <div style="flex:1; background:white; border-radius:8px; padding:8px;">
          <h3>Tomorrow</h3>
          <p><b>📅 Date:</b> ${tomorrow.date}</p>
          <p><b>🌡️ Max:</b> ${tomorrow.max}°C</p>
          <p><b>🌡️ Min:</b> ${tomorrow.min}°C</p>
          <p><b>💨 Wind:</b> ${tomorrow.wind} km/h</p>
          <p><b>Condition:</b> ${getWeatherEmoji(tomorrow.code)}</p>
        </div>
      </div>

      <p style="text-align:center; margin-top:10px; font-size:12px; color:#555;">
        Generated at ${current.time}
      </p>
    </div>
  </div>
  `
}];
